sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var inputValue = parseFloat(newValue);

    // Check if value is below 10%
    if (!isNaN(inputValue) && inputValue < 10) {
        g_form.addInfoMessage("Battery critically low ( " + inputValue + "% ) â€” mobile charging will be dispatched.");
    } else {
        g_form.clearMessages();
    }
}
