sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Check if the new value is less than 0 or greater than 100
    if (newValue < 0 || newValue > 100) {
        // Display an error message indicating the 0-100% range
        g_form.showFieldMsg("battery_percentage", "Invalid battery percentage (valid range 0 - 100%)", "error");
    } else {
        // Hide any existing error message if the value is valid
        g_form.hideFieldMsg("battery_percentage", true);
    }
}
