sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below

    if (g_user.hasRole("x_cdltd_chargesos.user") && (!g_user.hasRole("x_cdltd_chargesos.operator") && !g_user.hasRole("x_cdltd_chargesos.admin"))) {
        g_form.setValue("user", g_user.userID);
        g_form.setReadOnly("user", true);
		//Make assigned operator read only
        g_form.setReadOnly("assigned_operator", true);
    }
}
