sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below

	// Assign the new value to a string
	var str = newValue;

	// This is a general regex (Regular Expression) for accepting only strings and numbers
	// var regex = /^\d+$/;
	var regex = /^[a-zA-Z0-9\s]+$/;

	// Check if the regex doesn't match the string and show the field msg
	if (!regex.test(str)) {
		g_form.showFieldMsg("license_plate", "Invalid License Plate", "error");
	} else {
		g_form.hideFieldMsg("license_plate");
	}
}
