sys_script_include.script:
var GetLocationDistrict = Class.create();
GetLocationDistrict.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getLocationDistrict: function (){
		// Get the sys ID of the location 
		var locationId = this.getParameter("sysparm_location_id");

		var locationGr = new GlideRecord("cmn_location");

		/*
		Below is another way to achieve what we want with the while loop and .next() function for the GlideRecord

		locationGr.addQuery("sys_id", locationId);
		locationGr.query();

		while(locationGr.next()) {
            var value = locationGr.u_district;
            return value; // return the source field value
		}
		*/

        if (locationGr.get("sys_id", locationId)) {
            var value = locationGr.getValue("u_district");
            return value; // return the source field value
        }
        return '';
	},
    type: 'GetLocationDistrict'
});
